package JavaBasics;

public class DataTypes {

	//Starting/execution point of the program
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Primitive data types i.e int, char, double, boolean
		
		//int: data type for integers only
		int i = 10;
		System.out.println(i);
		int j = 20;
			j = 30;
			
		System.out.println(i+j);
			
		//double: data type for all the decimal values
		double d1 = 10.33;
		double d2 = 100;
			   d2 = 100.01;
			   
		//char: data type for only single digit value and should be written with thne '' quotes
		char c1 = 'a';
		char c2 = 'b';
			 c2 = 1;
			 
		//boolean: data type is either true of false
		boolean b1 = true;
				b1 = false;
				
		//String is not a data type but it's a class but can be used as data type of a class
		String s1 = "hello";
		String s2 = "world hello";
		System.out.println(s2);
	}

}
